package ir.composeopeqe.commonimport androidx.compose.animation.core.*import androidx.compose.foundation.backgroundimport androidx.compose.foundation.layout.*import androidx.compose.foundation.shape.CircleShapeimport androidx.compose.foundation.shape.RoundedCornerShapeimport androidx.compose.runtime.Composableimport androidx.compose.ui.Modifierimport androidx.compose.ui.draw.clipimport androidx.compose.ui.geometry.Offsetimport androidx.compose.ui.graphics.Brushimport androidx.compose.ui.graphics.Colorimport androidx.compose.ui.unit.dp@Composablefun AnimatedShimmer() {    val shimmerColor = listOf(        Color.LightGray.copy(alpha = 0.8f),        Color.LightGray.copy(alpha = 0.2f) ,        Color.LightGray.copy(alpha = 0.8f)    )    val transition = rememberInfiniteTransition()    val translateAnimation = transition.animateFloat(        initialValue = 0f,        targetValue = 1000f,        animationSpec = infiniteRepeatable(             animation = tween(                 durationMillis = 1000 ,                 easing = FastOutSlowInEasing             )        )    )    val brush = Brush.linearGradient(        colors = shimmerColor ,        start = Offset.Zero ,        end = Offset(x = translateAnimation.value , y = translateAnimation.value)    )        shimmerGridItem(brush = brush)}@Composablefun shimmerGridItem(brush: Brush){    Row(        Modifier            .fillMaxSize()            .padding(start = 30.dp , end = 30.dp , top = 20.dp)    ) {        Spacer(modifier = Modifier            .size(45.dp)            .background(brush)            .clip(RoundedCornerShape(30.dp))        )        Column(verticalArrangement = Arrangement.Center ,            modifier = Modifier.padding(start = 20.dp , top = 10.dp)) {                        Spacer(modifier = Modifier                .height(20.dp)                .fillMaxWidth(fraction = 0.7f)                .background(brush)                .clip(CircleShape)                .padding(top = 30.dp)            )        }    }    }