package ir.composeopeqe.dataimport androidx.compose.runtime.collectAsStateimport ir.composeopeqe.data.db.DaoAppimport ir.composeopeqe.service.ApiServiceimport kotlinx.coroutines.flow.Flowimport kotlinx.coroutines.flow.collectimport kotlinx.coroutines.flow.flowimport javax.inject.Injectclass Repository @Inject constructor(private val daoApp: DaoApp , private val apiService: ApiService) {    suspend fun getListUserFromApi(): Users {        val users = apiService.getListUser(20)        daoApp.addUsers(users.results)        return users    }    suspend fun getAllUsers(): Flow<List<Result>> = flow {        daoApp.getAllUsers().collect{            emit(it)        }    }    suspend fun getUser(id : Long) : Result = daoApp.getUser(id)}