package ir.composeopeqe.data.dbimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport ir.composeopeqe.data.Resultimport ir.composeopeqe.data.Usersimport kotlinx.coroutines.flow.Flow@Daointerface DaoApp {    @Query("SELECT * FROM users")    fun getAllUsers() : Flow<List<Result>>    @Insert(onConflict = OnConflictStrategy.REPLACE)    suspend fun addUsers(users : List<Result>)    @Query("SELECT * FROM users WHERE idd = :id")    suspend fun getUser(id : Long) : Result}